import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as r,a as e,b as s,d as t,e as d,r as n}from"./app.2ece7d8a.js";const l={},c=e("h1",{id:"custom-email-address",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#custom-email-address","aria-hidden":"true"},"#"),t(" Custom Email Address")],-1),h=t("When using your "),m=t(", we will need to send transactional emails to your users used for e.g. verification, password reset, welcome emails and the like."),u=d('<p>By default, we are sending these emails from <code>Your Company Name &lt;noreply@tough.zone&gt;</code>. But it is also possible to use custom sender addresses, depending on your needs.</p><p>To make this possible, you will first have to decide, which address you would like to use and then which of the both methods described below would fit your needs in terms of compliance, flexibility and features.</p><p>We are using <a href="https://aws.amazon.com/ses/" target="_blank" rel="noopener noreferrer">AWS SES</a> for email transport which dictates both processes described below. Learn more about both verification processes <a href="https://docs.aws.amazon.com/ses/latest/dg/creating-identities.html" target="_blank" rel="noopener noreferrer">here</a>.</p><p>We do not offer mailboxes, so if you do not maintain a properly configured mailbox for the address you desire, emails directed to that address are bounced by the outbound MTA of the sender and will not be delivered anywhere.</p><h2 id="domain-verification" tabindex="-1"><a class="header-anchor" href="#domain-verification" aria-hidden="true">#</a> Domain verification</h2><p>::: tip We generally recommend domain verification. :::</p><p>In this case we will verify a whole (sub-) domain using DNS records. Afterwards we will be able to send emails from any email address ending with that (sub-) domain.</p><h3 id="process" tabindex="-1"><a class="header-anchor" href="#process" aria-hidden="true">#</a> Process</h3><ol><li>You inform us about the address you would like to use (e.g. <code>noreply@your-domain.com</code>)</li><li>We start email domain verification with AWS and provide you with DKIM keys</li><li>You will have 48 hours to complete DNS configuration and add the provided keys to the DNS of <code>your-domain.com</code></li><li>The domain is verified and can be used for outbound emails</li></ol><h3 id="pros-and-cons" tabindex="-1"><a class="header-anchor" href="#pros-and-cons" aria-hidden="true">#</a> Pros and cons</h3><table><thead><tr><th style="text-align:center;">Pro</th><th style="text-align:center;">Con</th></tr></thead><tbody><tr><td style="text-align:center;">DKIM verified, emails are more likely not delivered to spam</td><td style="text-align:center;">DNS configuration required</td></tr><tr><td style="text-align:center;">DMARC and SPF compliance possible (please contact us)</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">Any address that ends with your domain instantly possible</td><td style="text-align:center;"></td></tr></tbody></table><h2 id="single-email-verification" tabindex="-1"><a class="header-anchor" href="#single-email-verification" aria-hidden="true">#</a> Single Email verification</h2><p>In this case we verify a specific email address like <code>noreply@your-domain.com</code>. Afterwards we will only be able to send emails from that specific email address.</p><h3 id="process-1" tabindex="-1"><a class="header-anchor" href="#process-1" aria-hidden="true">#</a> Process</h3><ol><li>You inform us about the address you would like to use (e.g. <code>noreply@your-domain.com</code>)</li><li>We start email address verification with AWS</li><li>AWS will send a verification email to your chosen email address</li><li>You will have 48 hours to click on the activation link provided by AWS</li><li>The email address is verified and can be used for outbound emails</li></ol><h3 id="pros-and-cons-1" tabindex="-1"><a class="header-anchor" href="#pros-and-cons-1" aria-hidden="true">#</a> Pros and cons</h3><table><thead><tr><th style="text-align:center;">Pro</th><th style="text-align:center;">Con</th></tr></thead><tbody><tr><td style="text-align:center;">Easier to set up</td><td style="text-align:center;">DMARC, DKIM and SPF not possible</td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;">Step needs to be repeated if desired address changes</td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;">You need to maintain an inbox for the desired address to receive the verification email</td></tr></tbody></table>',17);function f(p,y){const a=n("ToughZone");return i(),r("div",null,[c,e("p",null,[h,s(a),m]),u])}var w=o(l,[["render",f],["__file","CustomEmailAddress.html.vue"]]);export{w as default};
